mapper.select:
SELECT ID, SERIAL_NUMBER, ACCESS_NUMBER, DIRECTOR, STATUS
FROM EQUIPMENT
WHERE ID = #{id}

mapper.update:
UPDATE EQUIPMENT
SET SERIAL_NUMBER = #{equipment.serialNumber},
    ACCESS_NUMBER = #{equipment.accessNumber},
    DIRECTOR      = #{equipment.director},
    STATUS = #{equipment.status}
WHERE ID = #{equipment.id, jdbcType=BIGINT}

mapper.select:
SELECT SERIAL_NUMBER
FROM EQUIPMENT
WHERE SERIAL_NUMBER = #{serialNumber}

SELECT ACCESS_NUMBER
FROM EQUIPMENT
WHERE ACCESS_NUMBER = #{accessNumber}

mapper.update:
UPDATE EQUIPMENT
SET DIRECTOR = #{director}
WHERE ID = #{id, jdbcType=BIGINT}

UPDATE EQUIPMENT
SET STATUS = #{status}
WHERE ID = #{id, jdbcType=BIGINT}
